INSERT INTO `bx_dashiji` VALUES ('8', '1979', '1979', null, 'http://182.50.112.199:82/upload/userfiles/1/images/dsjphoto/2016/05/1979.png', '<p class=\"MsoNormal\" style=\"line-height: 200%; text-align: justify;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: \'Times New Roman\', serif; line-height: 21pt;\">Comrade Deng Xiaoping visited the Company&rsquo;s base and gave directive to &ldquo;vigorously develop new buildingmaterials business </span></span></p>\r\n<p align=\"left\" class=\"MsoNormal\" style=\"line-height: 21pt;\">\r\n	<span style=\"font-size:14px;\"><span lang=\"EN-US\" style=\"font-family: \'Times New Roman\', serif;\">and make the factory prosperous&rdquo;;</span></span><span lang=\"EN-US\" style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p>\r\n<p align=\"left\" class=\"MsoNormal\" style=\"line-height: 21pt;\">\r\n	<span style=\"font-size:14px;\"><span lang=\"EN-US\" style=\"font-family: \'Times New Roman\', serif;\">BNBM House completed the new materials experimental building; </span></span><span lang=\"EN-US\" style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p>\r\n<p align=\"left\" class=\"MsoNormal\" style=\"line-height: 21pt;\">\r\n	<span style=\"font-size:14px;\"><span lang=\"EN-US\" style=\"font-family: \'Times New Roman\', serif;\">BNBM House imported several production lines to produce new building materials; </span></span><span lang=\"EN-US\" style=\"font-family: Helvetica, sans-serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\" style=\"line-height: 21pt;\">\r\n	<span style=\"font-size:14px;\"><span lang=\"EN-US\" style=\"font-family: \'Times New Roman\', serif;\">BNBM House built a precast concrete housing factory with the annual capacity of 600,000 square meters.</span></span></p>');
INSERT INTO `bx_dashiji` VALUES ('9', '', '1983', null, '', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: \'Times New Roman\', serif; line-height: 200%;\">The then Premier Zhao Ziyang visited China New Building Materials Company (predecessor of CNBM) to give the preferential policies </span></span></p>\r\n<p class=\"MsoNormal\" style=\"line-height:200%\">\r\n	<span style=\"font-size:14px;\"><span lang=\"EN-US\" style=\"line-height: 200%; font-family: \'Times New Roman\', serif;\">beyond the plan to develop new building materials industry.</span></span><span lang=\"EN-US\" style=\"font-size:\r\n9.0pt;mso-bidi-font-size:11.0pt;line-height:200%;font-family:&quot;Times New Roman&quot;,serif\"><o:p></o:p></span></p>');
INSERT INTO `bx_dashiji` VALUES ('11', '', '1992', null, '', '<p class=\"MsoNormal\" style=\"text-align: justify;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: \'Times New Roman\', serif;\">The then General Secretary Jiang Zemin wrote the inscription for BNBM House: To vigorously promote the use of new building </span></span><span style=\"font-family: \'Times New Roman\', serif;\">materials.</span></p>');
INSERT INTO `bx_dashiji` VALUES ('10', '', '1993', null, '', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: \'Times New Roman\', serif;\">BNBM House established the steel structure housing production lines of portal frame and rock wool system.</span></span></p>');
INSERT INTO `bx_dashiji` VALUES ('12', '', '1997', null, '', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: \'Times New Roman\', serif;\">BNBM House built the light-gauge steel structure residence demonstration houses.</span></span></p>');
INSERT INTO `bx_dashiji` VALUES ('13', '', '2002', null, 'http://182.50.112.199:82/upload/userfiles/1/images/dsjphoto/2016/06/deed_2002_452x306.png', '<p class=\"MsoNormal\">\r\n	<span style=\"font-family: \'Times New Roman\', serif;\">BNBM House was authorized among the first batch of &ldquo;State Housing Industrialization Bases&rdquo; by the then Ministry of Construction.</span></p>\r\n<p class=\"MsoNormal\">\r\n	<span lang=\"EN-US\" style=\"font-family: \'Times New Roman\', serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family: \'Times New Roman\', serif;\">BNBM House cooperated with Mitsubishi Corporation, Nippon Steel Corporation and Toyota Motor Corporation to build the first </span></p>\r\n<p class=\"MsoNormal\">\r\n	<span lang=\"EN-US\" style=\"font-family: \'Times New Roman\', serif;\">light-gauge steel structure residential demonstration house.</span></p>');
INSERT INTO `bx_dashiji` VALUES ('14', '', '2003', null, 'http://182.50.112.199:82/upload/userfiles/1/images/dsjphoto/2016/05/deed2003(1).png', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span lang=\"EN-US\" style=\"font-family: \'Times New Roman\', serif;\">BNBM House compiled the first Technical Specification for Steel-Framed Building System with Light-gauge Steel Shapes.</span></span><span lang=\"EN-US\" style=\"font-family:\r\n&quot;Times New Roman&quot;,serif\"><o:p></o:p></span></p>');
INSERT INTO `bx_dashiji` VALUES ('15', '', '2004', null, 'http://182.50.112.199:82/upload/userfiles/1/images/dsjphoto/2016/05/bx_dasj2004.png', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: 微软雅黑, sans-serif;\">BNBM House built the first thin-walled steel villa community in China: Sakura Villa Project in Dalian, Liaoning </span><span style=\"font-family: 微软雅黑, sans-serif;\">Province.</span></span></p>');
INSERT INTO `bx_dashiji` VALUES ('16', '', '2005', null, 'http://182.50.112.199:82/upload/userfiles/1/images/dsjphoto/2016/05/bx_dasj2005.png', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: \'Times New Roman\', serif;\">BNBM House built the first Chinese style thin-walled steel villa community: Shanshui Longcheng Villa Project in Wuhan.</span></span></p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span lang=\"EN-US\" style=\"font-family: \'Times New Roman\', serif;\"><o:p></o:p></span></span></p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: \'Times New Roman\', serif;\">BNBM House undertook residence construction aid project after Tsunami in Southeast Asia.</span></span></p>\r\n<p class=\"MsoNormal\">\r\n	&nbsp;</p>');
INSERT INTO `bx_dashiji` VALUES ('17', '', '2007', null, '', '<p class=\"MsoNormal\">\r\n	<span style=\"font-family: \'Times New Roman\', serif;\">BNBM House promoted its new housing technology in seven overseas countries;</span></p>\r\n<p class=\"MsoNormal\">\r\n	<span lang=\"EN-US\" style=\"font-family: \'Times New Roman\', serif;\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-family: \'Times New Roman\', serif;\">BNBM House undertook hospital construction aid project in Sudan.</span></p>\r\n<p class=\"MsoNormal\">\r\n	&nbsp;</p>');
