INSERT INTO `bx_dashiji` VALUES ('21', '', '2011', null, 'http://182.50.112.199:82/upload/userfiles/1/images/dsjphoto/2016/05/bx_dasj2011.png', '<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span lang=\"EN-US\" style=\"font-family: \'Times New Roman\', serif;\">BNBM House undertook construction of the new style housing project of the largest single building area in the world &mdash; Zambia </span></span></p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span lang=\"EN-US\" style=\"font-family: \'Times New Roman\', serif;\">Community Project which includes about 4,500 houses;</span></span><span lang=\"EN-US\" style=\"font-size:9.0pt;font-family:&quot;Times New Roman&quot;,serif\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span lang=\"EN-US\" style=\"font-family: \'Times New Roman\', serif;\">BNBM House started construction of its Chengdu Base in Sichuan Province;</span></span><span lang=\"EN-US\" style=\"font-size:9.0pt;font-family:&quot;Times New Roman&quot;,serif\"><o:p></o:p></span></p>\r\n<p class=\"MsoNormal\">\r\n	<span style=\"font-size:14px;\"><span lang=\"EN-US\" style=\"font-family: \'Times New Roman\', serif;\">BNBM House participated in post-disaster reconstruction after &ldquo;3.11&rdquo; Earthquake in Japan.</span></span><span lang=\"EN-US\" style=\"font-size:9.0pt;font-family:&quot;Times New Roman&quot;,serif\"><o:p></o:p></span></p>');
