INSERT INTO `cms_article_data` VALUES ('17c45e7ef68849d0b8388586c1133122', '<section class=\"yead_editor\" id=\"2_313\" label=\"Copyright © 2015 Yead All Rights Reserved.\" style=\"font-family: \'Microsoft YaHei\'; margin: 5px auto; white-space: normal;\">\r\n	<section style=\"width:100%;padding-bottom:5px;padding-top:5px;text-decoration:inherit\">\r\n		<section class=\"yead_bdlc\" style=\"line-height: 30px; border-left-width: 3px; border-left-style: solid; border-left-color: rgb(197, 63, 70); padding: 0px 0px 5px 20px;\">\r\n			<span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;\"><strong>材料房屋一体化 </strong></span></span>\r\n			<p>\r\n				<span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;\">北新房屋完全实现了&ldquo;材料房屋一体化&rdquo;。与国内众多的其他房屋公司集成采购的模式不同，北新房屋的主体结构及核心部品均采用中国建材旗下各生产企业自主生产的部品部件，以服务于奥运、世博、各地标志性建筑和五星级酒店的产品来确保房屋的整体品质，打造完整产业链。</span></span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<section class=\"yead_editor\" id=\"2_313\" label=\"Copyright © 2015 Yead All Rights Reserved.\" style=\"font-family: \'Microsoft YaHei\'; margin: 5px auto; white-space: normal;\">\r\n	<section style=\"width:100%;padding-bottom:5px;padding-top:5px;text-decoration:inherit\">\r\n		<section class=\"yead_bdlc\" style=\"line-height: 30px; border-left-width: 3px; border-left-style: solid; border-left-color: rgb(197, 63, 70); padding: 0px 0px 5px 20px;\">\r\n			<span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;\"><strong>设计生产安装一体化 </strong></span></span>\r\n			<p>\r\n				<span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;\">北新房屋对商业模式进行创新，将传统建筑商业模式&ldquo;设计、总包、专业分包、建材供应商、建材生产商&rdquo;五个环节进行整合，让客户节约精力、降低交易成本，为客户提供一站式专业化服务。</span></span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<section class=\"yead_editor\" id=\"2_313\" label=\"Copyright © 2015 Yead All Rights Reserved.\" style=\"font-family: \'Microsoft YaHei\'; margin: 5px auto; white-space: normal;\">\r\n	<section style=\"width:100%;padding-bottom:5px;padding-top:5px;text-decoration:inherit\">\r\n		<section class=\"yead_bdlc\" style=\"line-height: 30px; border-left-width: 3px; border-left-style: solid; border-left-color: rgb(197, 63, 70); padding: 0px 0px 5px 20px;\">\r\n			<span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;\"><strong>设计 </strong></span></span>\r\n			<p>\r\n				<span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;\">建筑设计甲级资质：可以为房屋使用者和项目开发商提供全套解决方案，确保产品的市场使用性和性能优势。</span></span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<section class=\"yead_editor\" id=\"2_313\" label=\"Copyright © 2015 Yead All Rights Reserved.\" style=\"font-family: \'Microsoft YaHei\'; margin: 5px auto; white-space: normal;\">\r\n	<section style=\"width:100%;padding-bottom:5px;padding-top:5px;text-decoration:inherit\">\r\n		<section class=\"yead_bdlc\" style=\"line-height: 30px; border-left-width: 3px; border-left-style: solid; border-left-color: rgb(197, 63, 70); padding: 0px 0px 5px 20px;\">\r\n			<span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;\"><strong>生产 </strong></span></span>\r\n			<p>\r\n				<span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;\">CAD及CAM软件为房屋产品工厂化生产、高精度制作、便捷的现场安装提供了保障</span></span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<section class=\"yead_editor\" id=\"2_313\" label=\"Copyright © 2015 Yead All Rights Reserved.\" style=\"font-family: \'Microsoft YaHei\'; margin: 5px auto; white-space: normal;\">\r\n	<section style=\"width:100%;padding-bottom:5px;padding-top:5px;text-decoration:inherit\">\r\n		<section class=\"yead_bdlc\" style=\"line-height: 30px; border-left-width: 3px; border-left-style: solid; border-left-color: rgb(197, 63, 70); padding: 0px 0px 5px 20px;\">\r\n			<span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;\"><strong>集成 </strong></span></span>\r\n			<p>\r\n				<span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;\">从供应商选择、生产管理、物流管理到应急计划，整个系统互相配合，构成了环环相扣的研制产品质量管理体系，使房屋产品获得从成本、时间、效率、质量等方面的最佳效果。</span></span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<section class=\"yead_editor\" id=\"2_313\" label=\"Copyright © 2015 Yead All Rights Reserved.\" style=\"font-family: \'Microsoft YaHei\'; margin: 5px auto; white-space: normal;\">\r\n	<section style=\"width:100%;padding-bottom:5px;padding-top:5px;text-decoration:inherit\">\r\n		<section class=\"yead_bdlc\" style=\"line-height: 30px; border-left-width: 3px; border-left-style: solid; border-left-color: rgb(197, 63, 70); padding: 0px 0px 5px 20px;\">\r\n			<span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;\"><strong>有效的管理协同 </strong></span></span>\r\n			<p>\r\n				<span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;\">拥有行业内具有丰富经验的管理团队，推行流程界面可视化管理，采用先进的管理模型思想和管理应用技术平台，高效有序的进行多项目协同管理，使项目目标与企业资源达到最优化匹配。</span></span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<section class=\"yead_editor\" id=\"2_313\" label=\"Copyright © 2015 Yead All Rights Reserved.\" style=\"font-family: \'Microsoft YaHei\'; margin: 5px auto; white-space: normal;\">\r\n	<section style=\"width:100%;padding-bottom:5px;padding-top:5px;text-decoration:inherit\">\r\n		<section class=\"yead_bdlc\" style=\"line-height: 30px; border-left-width: 3px; border-left-style: solid; border-left-color: rgb(197, 63, 70); padding: 0px 0px 5px 20px;\">\r\n			<span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;\"><strong>完善的售后服务 </strong></span></span>\r\n			<p>\r\n				<span style=\"font-size:12px;\"><span style=\"font-family:微软雅黑;\">定期对客户进行回访，对于用户提出的意见和建议及时反馈与处理。</span></span></p>\r\n		</section>\r\n	</section>\r\n</section>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('17f645d094ed4348afcbf397d3fc7930', '<p class=\"MsoNormal\" style=\"text-indent:0em;\">\r\n	<img alt=\"\" src=\"http://182.50.112.199:8081/userfiles/1/images/cms/article/2015/12/%E5%85%AC%E5%8F%B8%E7%AE%80%E4%BB%8B%E9%85%8D%E5%9B%BE.jpg\" style=\"width: 674px; height: 385px;\" /></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 24pt;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: 微软雅黑;\"><span style=\"color: rgb(51, 51, 51);\">北新房屋有限公司是中国建材集团成员企业，总投资20亿元人民币，由北新集团控股，是集团&ldquo;三新&rdquo;战略之新型房屋战略事业平台。</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0000pt;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: 微软雅黑;\"><span style=\"color: rgb(51, 51, 51);\">北新房屋业务开创于九十年代，多年来一直投身和致力于新型房屋产业的研制、推广、应用事业。公司拥有完整的新型房屋产业链，已建成北京、四川、江苏、黑龙江、海南等基地，并已形成全国性的产业布局。公司拥有全球先进的自主知识产权轻钢结构房屋技术，也是《低层冷弯薄壁型钢房屋建筑技术规程》JGJ&nbsp;227-2011（行业标准）的核心参编单位。公司于2002年底，被建设部（现住房和城乡建设部）确定为首批国家住宅产业化基地。</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0000pt;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: 微软雅黑;\"><span style=\"color: rgb(51, 51, 51);\">北新房屋完全实现了&ldquo;材料房屋一体化&rdquo;、&ldquo;设计生产施工一体化&rdquo;。公司在吸收世界先进技术的基础上，历经数十年的探索和发展，集成创新，持续优化，房屋体系全部实现工业化生产，并配以完善的供应链管理系统和专业的物流、安装服务体系，致力于为客户提供绿色、节能、低碳综合解决方案。</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent:20pt;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: 微软雅黑;\"><span style=\"color: rgb(51, 51, 51);\">公司通过长期的实践与积累，已在新城镇建设、住宅别墅、旅游度假、城市小建筑体等领域形成优势，并多次承接灾后援建任务，海外市场也屡获佳绩，在&ldquo;一带一路&rdquo;国家和地区项目顺利实施，产品和服务已遍布全球多个国家和地区。</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent:24.0000pt;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: 微软雅黑;\"><span style=\"color: rgb(51, 51, 51);\">北新房屋以引领新型房屋事业的蓬勃发展为己任，始终遵循国家住宅产业化整体发展战略，秉承&ldquo;善用资源、服务建设&rdquo;的产业理念，致力于为建设生态文明、构建美丽中国做出贡献。</span></span></span></p>\r\n<p>\r\n	<!--[if gte mso 9]><xml><w:LatentStyles Def