INSERT INTO `cms_article_data` VALUES ('4246fef3228b48c1ae092d5313458725', '<div class=\"wx96Diy\" data-source=\"bj.96weixin.com\">\r\n	<div class=\"96wxDiy\" style=\"margin: 1em auto; font-family: \'宋体\'; \">\r\n		<div style=\"width:100%; height:auto;margin: 0 auto; \">\r\n			<div class=\"96wx-bdlc\" style=\"border-left-width: 5px; border-left-style: solid; border-left-color: #c2262a; padding-bottom: 5px;\">\r\n				<div class=\"96wx-bdbc\" style=\"border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: #c2262a;; margin: 0px 0px 10px; padding-left: 8px;\">\r\n					<div class=\"96wx-color\" style=\"font-family: \'Microsoft YaHei\'; color: #646464; line-height: 35px; font-weight: bold; font-size: 18px;\">\r\n						<span style=\"vertical-align: bottom; display: inline-block;  \">Ecological environmentally&nbsp;</span>friendly</div>\r\n				</div>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>\r\n<p style=\"text-align: justify;\">\r\n	<img alt=\"\" src=\"http://182.50.112.199:82/upload/userfiles/1/images/cms/article/2016/05/10(1).jpg\" style=\"font-family: inherit; font-size: 1em; height: 293px; font-weight: inherit; text-align: inherit; text-decoration: inherit; text-indent: 18.9333px; width: 500px; margin-left: 50px; margin-right: 50px;\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family: \'Times New Roman\', serif; font-size: 12pt; line-height: 150%; text-align: justify; text-indent: 14.2pt;\">Energy-efficiency: Complyin</span><span style=\"font-family: \'Times New Roman\', serif; font-size: 12pt; line-height: 150%; text-indent: 14.2pt;\">g with the s</span><span style=\"font-family: \'Times New Roman\', serif; font-size: 12pt; line-height: 150%; text-indent: 14.2pt;\">tandard of building energy efficiency of 75% and above.</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family: \'Times New Roman\', serif; font-size: 12pt; line-height: 150%; text-align: justify; text-indent: 14.2pt;\">Water-saving: Dry operation can save 90% of the&nbsp;</span><span style=\"font-family: \'Times New Roman\', serif; font-size: 12pt; line-height: 150%; text-indent: 14.2pt;\">water consumed in the traditional construction method.</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family: \'Times New Roman\', serif; font-size: 12pt; line-height: 150%; text-align: justify; text-indent: 14.2pt;\">Material-saving: More than 90% materials can be recycled to eliminate a large number of construction waste generated when the building comes to the end of its service life.</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family: \'Times New Roman\', serif; font-size: 12pt; line-height: 150%; text-align: justify; text-indent: 14.2pt;\">Land-saving: The carpet area is increased by 10% and more.</span></p>\r\n<p style=\"text-align: justify;\">\r\n	<span style=\"font-family: \'Times New Roman\', serif; font-size: 12pt; line-height: 150%; text-align: justify; text-indent: 14.2pt;\">Environmentally friendly materials: The house is built with 100% new materials and both its production and use can meet the requirements for environmental protection.</span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent: 14.2pt; line-height: 150%; text-align: justify;\">\r\n	&nbsp;</p>\r\n<div class=\"wx96Diy\" data-source=\"bj.96weixin.com\">\r\n	<div class=\"96wxDiy\" style=\"margin: 1em auto; font-family: \'宋体\'; \">\r\n		<div style=\"width:100%; height:auto;margin: 0 auto; \">\r\n			<div class=\"96wx-bdlc\" style=\"border-left-width: 5px; border-left-style: solid; border-left-color: #c2262a; padding-bottom: 5px;\">\r\n				<div class=\"96wx-bdbc\" style=\"border-bottom-width: 1px; border-bottom-style: solid; border-bottom-color: #c2262a;; margin: 0px 0px 10px; padding-left: 8px;\">\r\n					<div class=\"96wx-color\" style=\"font-family: \'Microsoft YaHei\'; color: #646464; line-height: 35px; font-weight: bold; font-size: 18px;\">\r\n						<span style=\"vertical-align: bot