INSERT INTO `cms_article_data` VALUES ('543642097a2f4be686334f4eb89d49d7', '<section class=\"yead_editor\" id=\"10_370\" label=\"Copyright © 2015 Yead All Rights Reserved.\" onmousedown=\"shifuMouseDown(\'10_370\')\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';margin: 5px auto;white-space: normal;\">\r\n	<section style=\"background:#fff;text-align:center;border-style:none;clear:both;overflow:hidden\">\r\n		<p>\r\n			&nbsp;</p>\r\n		<span class=\"yead_bdc\" style=\"margin-right: 6px; padding: 5px; border: 1px solid rgb(204, 204, 204); float: left;\"><img src=\"http://182.50.112.199:8081/userfiles/1/images/cms/article/2015/12/QQ%E6%88%AA%E5%9B%BE20151217000406.png\" style=\"display: block; width: 300px; height: 226px;\" /></span>\r\n		<section style=\"line-height:30px;text-align:left;font-size:14px\">\r\n			<p style=\"display:inline\">\r\n				&nbsp;</p>\r\n			<span style=\"margin-top:-20px;text-indent:0em\"><strong>生态环保</strong> </span>\r\n			<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; text-indent: 2em; line-height: 25px; font-family: \'Microsoft YaHei\', 宋体, HELVETICA; font-size: 12px; white-space: normal;\">\r\n				<span style=\"font-family: 微软雅黑;\">资源节约：节能、节水、节材、节地</span></p>\r\n			<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; text-indent: 2em; line-height: 25px; font-family: \'Microsoft YaHei\', 宋体, HELVETICA; font-size: 12px; white-space: normal;\">\r\n				<span style=\"font-family: 微软雅黑;\">环保材料：全部使用新型材料，生产与使用过程均满足环保要求</span></p>\r\n			<p class=\"MsoNormal\" style=\"margin: 0px; padding: 0px; text-indent: 2em; line-height: 25px; font-family: \'Microsoft YaHei\', 宋体, HELVETICA; font-size: 12px; white-space: normal;\">\r\n				<span style=\"font-family: 微软雅黑;\">循环利用：90%以上材料可回收、循环利用，消除建筑物终老时产生的大量建筑垃圾</span></p>\r\n			<p>\r\n				<span style=\"margin-top:-20px;text-indent:0em\">&nbsp;</span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<p>\r\n	&nbsp;</p>\r\n<section class=\"yead_editor\" id=\"10_371\" label=\"Copyright © 2015 Yead All Rights Reserved.\" onmousedown=\"shifuMouseDown(\'10_371\')\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';margin: 5px auto;white-space: normal;\">\r\n	<section style=\"background:#fff;text-align:center;border-style:none;clear:both;overflow:hidden\">\r\n		<span class=\"yead_bdc\" style=\"padding: 5px; margin-left: 6px; border: 1px solid rgb(204, 204, 204); float: right;\"><img src=\"http://182.50.112.199:8081/userfiles/1/images/cms/article/2015/12/123(1).png\" style=\"display: block; width: 300px; height: 198px;\" /></span>\r\n		<section style=\"line-height:1.5;text-align:left;font-size:14px\">\r\n			<p style=\"display:inline\">\r\n				&nbsp;</p>\r\n			<span style=\"margin-top: 35px;text-indent:0em\"><strong>结构安全</strong> </span><br />\r\n			<p>\r\n				<span style=\"margin-top: 35px;text-indent:0em\"><span style=\"font-family: 微软雅黑; font-size: 12px; line-height: 25px; text-indent: 24px; white-space: normal;\">95&nbsp;年结构安全设计。结构钢骨、螺栓均采用高性能防腐工艺（镀锌、镀铝锌、镀镁铝锌），防腐层的自修复功能可确保主体结构不被锈蚀。</span> </span></p>\r\n			<p>\r\n				<span style=\"margin-top: 35px;text-indent:0em\">&nbsp;</span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<p>\r\n	&nbsp;</p>\r\n<section class=\"yead_editor\" id=\"10_370\" label=\"Copyright © 2015 Yead All Rights Reserved.\" onmousedown=\"shifuMouseDown(\'10_370\')\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';margin: 5px auto;white-space: normal;\">\r\n	<section style=\"background:#fff;text-align:center;border-style:none;clear:both;overflow:hidden\">\r\n		<span class=\"yead_bdc\" style=\"margin-right: 6px; padding: 5px; border: 1px solid rgb(204, 204, 204); float: left;\"><img src=\"http://182.50.112.199:8081/userfiles/1/images/cms/article/2015/12/%E6%8A%97%E9%9C%87%E9%98%B2%E9%A3%8E(1).png\" style=\"display: block; width: 350px; height: 204px;\" /></span>\r\n		<section style=\"line-height:30px;text-align:left;font-size:14px\">\r\n			<p style=\"display:inline\">\r\n				&nbsp;</p>\r\n			<span style=\"margin-top:-20px;text-indent:0em\"><strong>抗震防风</strong> </span>\r\n			<p>\r\n				<span style=\"margin-top:-20px;text-indent:0em\">满足9度设防要求,抵御12级台风。整体采用柔性结构，自重轻（约为传统砖混结构的1/6-1/4），连接部位均采用专用配件可靠牢固连接，极大提高房屋的抗震防风性能。</span></p>\r\n			<p>\r\n				<span style=\"margin-top:-20px;text-indent:0em\">&nbsp;</span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<p>\r\n	&nbsp;</p>\r\n<section class=\"yead_editor\" id=\"10_371\" label=\"Copyright © 2015 Yead All Rights Reserved.\" onmousedown=\"shifuMouseDown(\'10_371\')\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';margin: 5px auto;white-space: normal;\">\r\n	<section style=\"background:#fff;text-align:center;border-style:none;clear:both;overflow:hidden\">\r\n		<span class=\"yead_bdc\" style=\"padding: 5px; margin-left: 6px; border: 1px solid rgb(204, 204, 204); float: right;\"><img src=\"http://182.50.112.199:8081/userfiles/1/images/cms/article/2015/12/fdsdfsdf.jpg\" style=\"display: block; width: 300px; height: 190px;\" /></span>\r\n		<section style=\"line-height:1.5;text-align:left;font-size:14px\">\r\n			<p style=\"display:inline\">\r\n				&nbsp;</p>\r\n			<span style=\"margin-top: 35px;text-indent:0em\"><strong>防火安全</strong> </span>\r\n			<p>\r\n				<span style=\"margin-top: 35px;text-indent:0em\">采用复合型围护墙体，全面采用A级不燃型防火材料，外墙耐火极限超过2小时，内墙耐火极限超过1小时，满足建筑的防火设计标准。 </span></p>\r\n			<p>\r\n				<span style=\"margin-top: 35px;text-indent:0em\">&nbsp;</span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<p>\r\n	&nbsp;</p>\r\n<section class=\"yead_editor\" id=\"10_370\" label=\"Copyright © 2015 Yead All Rights Reserved.\" onmousedown=\"shifuMouseDown(\'10_370\')\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';margin: 5px auto;white-space: normal;\">\r\n	<section style=\"background:#fff;text-align:center;border-style:none;clear:both;overflow:hidden\">\r\n		<span class=\"yead_bdc\" style=\"margin-right: 6px; padding: 5px; border: 1px solid rgb(204, 204, 204); float: left;\"><img src=\"http://182.50.112.199:8081/userfiles/1/images/cms/article/2015/12/%E4%BF%9D%E6%B8%A9%E9%9A%94%E7%83%AD(1).png\" style=\"display: block; width: 300px; height: 174px;\" /></span>\r\n		<section style=\"line-height:30px;text-align:left;font-size:14px\">\r\n			<p style=\"display:inline\">\r\n				&nbsp;</p>\r\n			<span style=\"margin-top:-20px;text-indent:0em\"><strong>保温隔热</strong> </span>\r\n			<p>\r\n				<span style=\"margin-top:-20px;text-indent:0em\">&ldquo;双保温&rdquo;＋&ldquo;双隔层&rdquo;墙体保温节能技术，&ldquo;独有的热反射及通风间层设计，使得建筑围护隔热效果更优越，杜绝冷热桥现象，满足建筑节能75% 以上要求，确保室内冬暖夏凉。</span></p>\r\n			<p>\r\n				<span style=\"margin-top:-20px;text-indent:0em\">&nbsp;</span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<p>\r\n	&nbsp;</p>\r\n<section class=\"yead_editor\" id=\"10_371\" label=\"Copyright © 2015 Yead All Rights Reserved.\" onmousedown=\"shifuMouseDown(\'10_371\')\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';margin: 5px auto;white-space: normal;\">\r\n	<section style=\"background:#fff;text-align:center;border-style:none;clear:both;overflow:hidden\">\r\n		<span class=\"yead_bdc\" style=\"padding: 5px; margin-left: 6px; border: 1px solid rgb(204, 204, 204); float: right;\"><img src=\"http://182.50.112.199:8081/userfiles/1/images/cms/article/2015/12/fhgfghdf.jpg\" style=\"display: block; width: 300px; height: 212px;\" /></span>\r\n		<section style=\"line-height:1.5;text-align:left;font-size:14px\">\r\n			<p style=\"display:inline\">\r\n				&nbsp;</p>\r\n			<span style=\"margin-top: 35px;text-indent:0em\"><strong>通风防潮</strong> </span>\r\n			<p>\r\n				<span style=\"margin-top: 35px;text-indent:0em\">独特的开放式通气层设计，利用热空气对流原理，实现外墙通风防潮；主体结构与外墙装饰层之间加设&ldquo;单向呼吸纸&rdquo;，有效阻止潮气或其他腐蚀性气体侵入主体结构。北方地区冬天不会结露。 </span></p>\r\n			<p>\r\n				<span style=\"margin-top: 35px;text-indent:0em\">&nbsp;</span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<p>\r\n	&nbsp;</p>\r\n<section class=\"yead_editor\" id=\"10_370\" label=\"Copyright © 2015 Yead All Rights Reserved.\" onmousedown=\"shifuMouseDown(\'10_370\')\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';margin: 5px auto;white-space: normal;\">\r\n	<section style=\"background:#fff;text-align:center;border-style:none;clear:both;overflow:hidden\">\r\n		<span class=\"yead_bdc\" style=\"margin-right: 6px; padding: 5px; border: 1px solid rgb(204, 204, 204); float: left;\"><img src=\"http://182.50.112.199:8081/userfiles/1/images/cms/article/2015/12/%E9%9A%94%E9%9F%B3%E9%99%8D%E5%99%AA.jpg\" style=\"display: block; width: 300px; height: 169px;\" /></span>\r\n		<section style=\"line-height:30px;text-align:left;font-size:14px\">\r\n			<p style=\"display:inline\">\r\n				&nbsp;</p>\r\n			<span style=\"margin-top:-20px;text-indent:0em\"><strong>隔声降噪</strong> </span>\r\n			<p>\r\n				<span style=\"margin-top:-20px;text-indent:0em\">不同材质、不同厚度、多层结构及多层空腔组合成的复合墙体，所有内外墙体及楼板内填充隔声材料，外墙隔声量大于55dB,内墙隔声量大于52dB，超过五星级酒店的隔声标准。</span></p>\r\n			<p style=\"display:inline\">\r\n				&nbsp;</p>\r\n			<span style=\"margin-top: 35px;text-indent:0em\"><strong>布局灵活</strong> </span>\r\n			<p>\r\n				<span style=\"margin-top: 35px;text-indent:0em\">墙体厚度约为传统结构的1/2，套内面积增加10%以上，得房率最高达92%。整体以外墙承重为主，室内空间可灵活布局。楼板无主梁，楼层净高增加200mm以上。 </span></p>\r\n			<p>\r\n				<span style=\"margin-top:-20px;text-indent:0em\">&nbsp;</span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<p>\r\n	&nbsp;</p>\r\n<section class=\"yead_editor\" id=\"10_370\" label=\"Copyright © 2015 Yead All Rights Reserved.\" onmousedown=\"shifuMouseDown(\'10_370\')\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';margin: 5px auto;white-space: normal;\">\r\n	<section style=\"background:#fff;text-align:center;border-style:none;clear:both;overflow:hidden\">\r\n		<span class=\"yead_bdc\" style=\"margin-right: 6px; padding: 5px; border: 1px solid rgb(204, 204, 204); float: left;\"><img src=\"http://182.50.112.199:8081/userfiles/1/images/cms/article/2015/12/%E7%BE%8E%E4%B8%BD%E5%A4%96%E8%A7%82.jpg\" style=\"display: block; width: 300px; height: 228px;\" /></span>\r\n		<section style=\"line-height:30px;text-align:left;font-size:14px\">\r\n			<p style=\"display:inline\">\r\n				&nbsp;</p>\r\n			<span style=\"margin-top:-20px;text-indent:0em\"><strong>美丽外观</strong> </span>\r\n			<p>\r\n				<span style=\"margin-top:-20px;text-indent:0em\">从传统中国民居（例如川东、徽派、维派民居）到现代欧美风情，北新房屋魅力打造多种建筑风格。</span></p>\r\n			<p>\r\n				<span style=\"margin-top:-20px;text-indent:0em\">&nbsp;</span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<p>\r\n	&nbsp;</p>\r\n<section class=\"yead_editor\" id=\"10_371\" label=\"Copyright © 2015 Yead All Rights Reserved.\" onmousedown=\"shifuMouseDown(\'10_371\')\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';margin: 5px auto;white-space: normal;\">\r\n	<section style=\"background:#fff;text-align:center;border-style:none;clear:both;overflow:hidden\">\r\n		<span class=\"yead_bdc\" style=\"padding: 5px; margin-left: 6px; border: 1px solid rgb(204, 204, 204); float: right;\"><img src=\"http://182.50.112.199:8081/userfiles/1/images/cms/article/2015/12/%E4%BD%BF%E7%94%A8%E5%B9%BF%E6%B3%9B.jpg\" style=\"display: block; width: 300px; height: 228px;\" /></span>\r\n		<section style=\"line-height:1.5;text-align:left;font-size:14px\">\r\n			<p style=\"display:inline\">\r\n				&nbsp;</p>\r\n			<span style=\"margin-top: 35px;text-indent:0em\"><strong>适用广泛</strong> </span>\r\n			<p>\r\n				<span style=\"margin-top: 35px;text-indent:0em\">适用于不同气候（严寒、热带）、各种地理条件；各种地形地貌：海岛、山地、平地；各种地质条件：沙漠、岩石、软土。</span></p>\r\n			<p>\r\n				<span style=\"margin-top: 35px;text-indent:0em\">&nbsp;</span></p>\r\n		</section>\r\n	</section>\r\n</section>\r\n<p>\r\n	&nbsp;</p>\r\n<section class=\"yead_editor\" id=\"10_370\" label=\"Copyright © 2015 Yead All Rights Reserved.\" onmousedown=\"shifuMouseDown(\'10_370\')\" style=\"font-size:14px;font-family:\'Microsoft YaHei\';margin: 5px auto;white-space: normal;\">\r\n	<section style=\"background:#fff;text-align:center;border-style:none;clear:both;overflow:hidden\">\r\n		<span class=\"yead_bdc\" style=\"margin-right: 6px; padding: 5px; border: 1px solid rgb(204, 204, 204); float: left;\"><img src=\"http://182.50.112.199:8081/userfiles/1/images/cms/article/2015/12/%E6%9C%AA%E6%A0%87%E9%A2%98-1.jpg\" style=\"display: block; width: 300px; height: 212px;\" /></span>\r\n		<section style=\"line-height:30px;text-align:left;font-size:14px\">\r\n			<p style=\"display:inline\">\r\n				&nbsp;</p>\r\n			<span style=\"margin-top:-20px;text-indent:0em\"><strong>施工便捷</strong> </span>\r\n			<p>\r\n				工厂化预制、现场拼装，缩短施工工期。如一栋200㎡的独立住宅，5～7天即可完成主体结构安装，2个月即可完成室内外精装修； 干法作业，北方冬季也可施工； 施工现场环保，无噪音、无粉尘、无污水污染。</p>\r\n			<p>\r\n				<span style=\"margin-top:-20px;text-indent:0em\">&nbsp;</span></p>\r\n		</section>\r\n	</section>\r\n</section>', '', '', '1');
