INSERT INTO `cms_article_data` VALUES ('8b1f14885ec14b7ab00ef11f7bc4f59f', '<p>\r\n	根据兵团党委六届十四次全委(扩大)会议部署，兵团发展改革委会同民政局、人力资源和社会保障局、国土资源局和建设局，指导2个国家级新型城镇化综合试点单位(阿拉尔市、北泉镇)、5个兵团级新型城镇化综合试点单位(铁门关市、双河市、五家渠市、石河子市、芳草湖农场)开展了试点工作2015年度总结评估，分析了试点推进情况，归纳了取得的成效，探析了存在的主要问题，提出了2016年工作思路及相关建议，近期将形成总结评估报告上报兵团。开展试点总结评估工作，对兵团新型城镇化探索发展新机制、总结发展新经验具有重要作用。</p>', '发改委网站', '', '1');
