INSERT INTO `cms_article_data` VALUES ('a76783aeb8d449d6a3586419faf73f96', '<p>\r\n	11月26日，江西省委农工部刘伟副巡视员、省住建厅副厅长李道鹏率江西省吉安市领导、井冈山市领导一行考察北新房屋密云基地。北新集团总经理陶铮、北新房屋总经理尹稷华在北新国家住宅产业化基地新型房屋体验区接待了客人一行，并详细介绍了北新房屋发展的相关情况。</p>\r\n<p>\r\n	随后，江西省领导一行参观了密云石城镇新村建设项目。考察后，江西省领导对北新房屋给予了高度评价，并表示将对未来的合作高度重视，共同做好示范工作。</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" src=\"http://182.50.112.199:8081/userfiles/1/images/cms/article/2015/12/%E8%80%83%E5%AF%9F%E7%85%A7%E7%89%87.jpg\" style=\"width: 680px; height: 907px;\" /></p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('a7e97c7535a9458db398b8982c74a828', '<p>\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: 微软雅黑;\"><!--[if gte mso 9]><xml><w:WordDocument><w:BrowserLevel>MicrosoftInternetExplorer4</w:BrowserLevel><w:DisplayHorizontalDrawingGridEvery>0</w:DisplayHorizontalDrawingGridEvery><w:DisplayVerticalDrawingGridEvery>2</w:DisplayVerticalDrawingGridEvery><w:DocumentKind>DocumentNotSpecified</w:DocumentKind><w:DrawingGridVerticalSpacing>7.8 磅</w:DrawingGridVerticalSpacing><w:PunctuationKerning></w:PunctuationKerning><w:View>Normal</w:View><w:Compatibility><w:DontGrowAutofit/><w:BalanceSingleByteDoubleByteWidth/><w:DoNotExpandShiftReturn/><w:UseFELayout/></w:Compatibility><w:Zoom>0</w:Zoom></w:WordDocument></xml><![endif]--></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent:28.0000pt;mso-char-indent-count:2.0000;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: 微软雅黑;\"><span style=\"color: rgb(37, 37, 37);\">根据兵团党委六届十四次全委（扩大）会议部署，兵团发展改革委会同民政局、人力资源和社会保障局、国土资源局和建设局，指导2个国家级新型城镇化综合试点单位（阿拉尔市、北泉镇）、5个兵团级新型城镇化综合试点单位（铁门关市、双河市、五家渠市、石河子市、芳草湖农场）开展了试点工作2015年度总结评估，分析了试点推进情况，归纳了取得的成效，探析了存在的主要问题，提出了2016年工作思路及相关建议，近期将形成总结评估报告上报兵团。开展试点总结评估工作，对兵团新型城镇化探索发展新机制、总结发展新经验具有重要作用。</span></span></span></p>\r\n<p class=\"MsoNormal\" style=\"text-indent:28.0000pt;mso-char-indent-count:2.0000;\">\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: 微软雅黑;\"><span style=\"color: rgb(37, 37, 37);\">&nbsp;</span></span></span></p>\r\n<p>\r\n	<span style=\"font-size:14px;\"><span style=\"font-family: 微软雅黑;\"><!--[if gte mso 9]><xml><w:LatentStyles DefLockedState=\"false\"  DefUnhideWhenUsed=\"true\"  DefSemiHidden=\"true\"  DefQFormat=\"false\"  DefPriority=\"99\"  LatentStyleCount=\"260\" >\r\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"Normal\" ></w:LsdException>\r\n<w:LsdException Locked=\"false\"  Priority=\"99\"  SemiHidden=\"false\"  Name=\"heading 1\" ></w:LsdException>\r\n<w:LsdE