INSERT INTO `cms_article_data` VALUES ('dd11e43b66c14f608d9c4810ec33879e', '<div>\r\n	<p>\r\n		<span style=\"font-family:微软雅黑;\"><span style=\"font-size:12px;\">在&ldquo;革新&rdquo;使命的指引下，在国企职责的感召下，在国家倡导低碳、节能减排的政策背景下，中国建材集团旗下骨干企业&mdash;&mdash;北新建材（集团）有限公司（以下简称&ldquo;北新集团&rdquo;）再次成为了重大历史号召的积极响应者与坚定的执行者。</span></span></p>\r\n</div>\r\n<div style=\"margin-top:13px;\">\r\n	<h3>\r\n		&nbsp;</h3>\r\n</div>\r\n<p>\r\n	&nbsp;</p>', '', '', '1');
INSERT INTO `cms_article_data` VALUES ('de88ba14830b495e8365bba7f7d85507', '<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; line-height: 30px; color: rgb(30, 30, 30); font-family: 宋体; white-space: normal; text-align: justify;\">\r\n	&nbsp; &nbsp;</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; line-height: 30px; color: rgb(30, 30, 30); font-family: 宋体; white-space: normal; text-align: justify;\">\r\n	青海新闻网讯 西宁(国家级)经济技术开发区生物科技产业园区围绕主导产业，不断增强科技创新动力，通过政府、企业、科研院所、高校、创客等多方协作，努力建设全省自主创新领航区、高新产业集聚区、创新创业首选区和创新型特色园区。</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; line-height: 30px; color: rgb(30, 30, 30); font-family: 宋体; white-space: normal; text-align: justify;\">\r\n	今年以来，生物科技产业园加快推进国家地方联合工程实验室、藏医药传承创新公共服务平台、机械装备工程技术研究中心等平台的资源整合，大力支持中国科学院、中国地质大学等单位建设中试平台，建设一批专业化、市场化、规模化的公共研发平台。以园区现有院士企业工作站为依托，鼓励支持骨干企业建设博士后工作站。依托全省首个国家重点实验室&mdash;&mdash;&ldquo;三江源生态与高原农牧业国家重点实验室&rdquo;以及全省首个企业自建的&ldquo;藏药新药开发国家重点实验室&rdquo;，加快推进产学研合作。重点推动国家级孵化器、青海青年创业园等&ldquo;双创&rdquo;平台升级建设，形成&ldquo;产学研用&rdquo;贯通的众创空间。</p>\r\n<p style=\"box-sizing: border-box; margin: 0px 0px 10px; padding: 0px; line-height: 30px; color: rgb(30, 30, 30); font-family: 宋体; white-space: normal; text-align: justify;\">\r\n	此外，根据省、市政府有关创新改造提升方案，加快推进康美中药城、牦牛产业示范园区等改造提升项目以及园区企业34个技术改造项目的建设进度，不断做大增量、做优存量。</p>', '三合亿官网', null, '1');
